import React, { useState, useEffect } from 'react';
import './App.css';
import {
  BarChart, Bar, LineChart, Line, PieChart, Pie, Cell,
  XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer
} from 'recharts';

// üîπ Configuraci√≥n de la URL base del API
const API_URL = 'http://localhost:5000/api/indicadores';

// Colores para los gr√°ficos
const COLORS = ['#0088FE', '#00C49F', '#FFBB28', '#FF8042', '#8884D8', '#82CA9D', '#FFC658'];

function App() {
  const [indicadores, setIndicadores] = useState([]);
  const [formData, setFormData] = useState({
    codigo_indicador: '',
    nombre_indicador: '',
    unidad_medida: '',
    descripcion: '',
    id_meta: ''
  });
  const [editando, setEditando] = useState(null);
  const [vistaActual, setVistaActual] = useState('tabla'); // 'tabla' o 'graficos'

  // üîπ Cargar indicadores al montar el componente
  useEffect(() => {
    fetchIndicadores();
  }, []);

  // üîπ Obtener todos los indicadores
  const fetchIndicadores = async () => {
    try {
      console.log('üîç Fetching indicadores from:', API_URL);
      const response = await fetch(API_URL);
      console.log('üì° Response status:', response.status);

      if (!response.ok) throw new Error(`HTTP error! status: ${response.status}`);

      const data = await response.json();
      console.log('‚úÖ Indicadores recibidos:', data);
      setIndicadores(data);
    } catch (error) {
      console.error('‚ùå Error al obtener indicadores:', error);
      alert('Error al cargar los indicadores: ' + error.message);
    }
  };

  // üîπ Manejar cambios en el formulario
  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  // üîπ Crear o actualizar indicador
  const handleSubmit = async (e) => {
    e.preventDefault();

    console.log('üì§ Enviando datos:', formData);

    try {
      const url = editando ? `${API_URL}/${editando}` : API_URL;
      const method = editando ? 'PUT' : 'POST';

      console.log(`üöÄ ${method} request to:`, url);

      const response = await fetch(url, {
        method: method,
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(formData)
      });

      console.log('üì° Response status:', response.status);

      const responseData = await response.json();
      console.log('üì¶ Respuesta completa:', responseData);

      if (!response.ok) {
        throw new Error(responseData.error || `Error ${response.status}: ${JSON.stringify(responseData)}`);
      }

      alert(editando ? 'Indicador actualizado' : 'Indicador creado');

      setFormData({
        codigo_indicador: '',
        nombre_indicador: '',
        unidad_medida: '',
        descripcion: '',
        id_meta: ''
      });
      setEditando(null);

      console.log('üîÑ Recargando indicadores...');
      await fetchIndicadores();

    } catch (error) {
      console.error('‚ùå Error completo:', error);
      alert('Error: ' + error.message);
    }
  };

  // üîπ Editar indicador
  const handleEdit = (indicador) => {
    setFormData({
      codigo_indicador: indicador.codigo_indicador,
      nombre_indicador: indicador.nombre_indicador,
      unidad_medida: indicador.unidad_medida || '',
      descripcion: indicador.descripcion || '',
      id_meta: indicador.id_meta || ''
    });
    setEditando(indicador.id_indicador);
  };

  // üîπ Eliminar indicador
  const handleDelete = async (id) => {
    if (!window.confirm('¬øEst√°s seguro de eliminar este indicador?')) return;

    try {
      const response = await fetch(`${API_URL}/${id}`, {
        method: 'DELETE'
      });

      if (!response.ok) {
        const error = await response.json();
        throw new Error(error.error || 'Error al eliminar');
      }

      alert('Indicador eliminado');
      fetchIndicadores();

    } catch (error) {
      console.error('Error al eliminar:', error);
      alert(error.message);
    }
  };

  // üîπ Cancelar edici√≥n
  const handleCancel = () => {
    setFormData({
      codigo_indicador: '',
      nombre_indicador: '',
      unidad_medida: '',
      descripcion: '',
      id_meta: ''
    });
    setEditando(null);
  };

  // üîπ Preparar datos para gr√°ficos
  const prepararDatosGraficos = () => {
    // Contar indicadores por meta
    const porMeta = indicadores.reduce((acc, ind) => {
      const meta = `Meta ${ind.id_meta}`;
      acc[meta] = (acc[meta] || 0) + 1;
      return acc;
    }, {});

    const dataPorMeta = Object.entries(porMeta).map(([name, value]) => ({
      name,
      cantidad: value
    }));

    // Contar indicadores por unidad de medida
    const porUnidad = indicadores.reduce((acc, ind) => {
      const unidad = ind.unidad_medida || 'Sin unidad';
      acc[unidad] = (acc[unidad] || 0) + 1;
      return acc;
    }, {});

    const dataPorUnidad = Object.entries(porUnidad).map(([name, value]) => ({
      name,
      cantidad: value
    }));

    return { dataPorMeta, dataPorUnidad };
  };

  const { dataPorMeta, dataPorUnidad } = prepararDatosGraficos();

  return (
    <div className="App">
      <h1>Gesti√≥n de Indicadores de Salud - ODS 3</h1>

      {/* Botones de navegaci√≥n */}
      <div className="nav-buttons">
        <button 
          className={vistaActual === 'tabla' ? 'active' : ''}
          onClick={() => setVistaActual('tabla')}
        >
          üìã Gesti√≥n de Datos
        </button>
        <button 
          className={vistaActual === 'graficos' ? 'active' : ''}
          onClick={() => setVistaActual('graficos')}
        >
          üìä Gr√°ficos y Estad√≠sticas
        </button>
      </div>

      {vistaActual === 'tabla' ? (
        <>
          {/* Formulario */}
          <form onSubmit={handleSubmit}>
            <input
              type="text"
              name="codigo_indicador"
              placeholder="C√≥digo del Indicador"
              value={formData.codigo_indicador}
              onChange={handleChange}
              required
            />
            <input
              type="text"
              name="nombre_indicador"
              placeholder="Nombre del Indicador"
              value={formData.nombre_indicador}
              onChange={handleChange}
              required
            />
            <input
              type="number"
              name="id_meta"
              placeholder="ID Meta"
              value={formData.id_meta}
              onChange={handleChange}
              required
            />
            <input
              type="text"
              name="unidad_medida"
              placeholder="Unidad de Medida"
              value={formData.unidad_medida}
              onChange={handleChange}
            />
            <textarea
              name="descripcion"
              placeholder="Descripci√≥n"
              value={formData.descripcion}
              onChange={handleChange}
            />
            <div>
              <button type="submit">
                {editando ? 'Actualizar' : 'Agregar'} Indicador
              </button>
              {editando && (
                <button type="button" onClick={handleCancel}>
                  Cancelar
                </button>
              )}
            </div>
          </form>

          {/* Tabla de indicadores */}
          <table>
            <thead>
              <tr>
                <th>C√≥digo</th>
                <th>Nombre</th>
                <th>Meta</th>
                <th>Unidad</th>
                <th>Descripci√≥n</th>
                <th>Acciones</th>
              </tr>
            </thead>
            <tbody>
              {indicadores.map((ind) => (
                <tr key={ind.id_indicador}>
                  <td>{ind.codigo_indicador}</td>
                  <td>{ind.nombre_indicador}</td>
                  <td>Meta {ind.id_meta}</td>
                  <td>{ind.unidad_medida}</td>
                  <td>{ind.descripcion}</td>
                  <td>
                    <button onClick={() => handleEdit(ind)}>Editar</button>
                    <button onClick={() => handleDelete(ind.id_indicador)}>
                      Eliminar
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </>
      ) : (
        <div className="charts-container">
          <h2>üìä Estad√≠sticas de Indicadores de Salud</h2>

          {/* Estad√≠sticas generales */}
          <div className="stats-grid">
            <div className="stat-card">
              <h3>Total de Indicadores</h3>
              <div className="stat-number">{indicadores.length}</div>
            </div>
            <div className="stat-card">
              <h3>Metas Diferentes</h3>
              <div className="stat-number">{dataPorMeta.length}</div>
            </div>
            <div className="stat-card">
              <h3>Unidades de Medida</h3>
              <div className="stat-number">{dataPorUnidad.length}</div>
            </div>
          </div>

          {/* Gr√°ficos */}
          <div className="charts-grid">
            {/* Gr√°fico de barras - Indicadores por Meta */}
            <div className="chart-card">
              <h3>Indicadores por Meta ODS</h3>
              <ResponsiveContainer width="100%" height={300}>
                <BarChart data={dataPorMeta}>
                  <CartesianGrid strokeDasharray="3 3" />
                  <XAxis dataKey="name" />
                  <YAxis />
                  <Tooltip />
                  <Legend />
                  <Bar dataKey="cantidad" fill="#0088FE" name="Cantidad de Indicadores" />
                </BarChart>
              </ResponsiveContainer>
            </div>

            {/* Gr√°fico circular - Distribuci√≥n por Meta */}
            <div className="chart-card">
              <h3>Distribuci√≥n por Meta</h3>
              <ResponsiveContainer width="100%" height={300}>
                <PieChart>
                  <Pie
                    data={dataPorMeta}
                    dataKey="cantidad"
                    nameKey="name"
                    cx="50%"
                    cy="50%"
                    outerRadius={80}
                    label
                  >
                    {dataPorMeta.map((entry, index) => (
                      <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
                    ))}
                  </Pie>
                  <Tooltip />
                  <Legend />
                </PieChart>
              </ResponsiveContainer>
            </div>

            {/* Gr√°fico de l√≠neas - Indicadores por Unidad */}
            <div className="chart-card">
              <h3>Indicadores por Unidad de Medida</h3>
              <ResponsiveContainer width="100%" height={300}>
                <LineChart data={dataPorUnidad}>
                  <CartesianGrid strokeDasharray="3 3" />
                  <XAxis dataKey="name" />
                  <YAxis />
                  <Tooltip />
                  <Legend />
                  <Line 
                    type="monotone" 
                    dataKey="cantidad" 
                    stroke="#00C49F" 
                    strokeWidth={2}
                    name="Cantidad"
                  />
                </LineChart>
              </ResponsiveContainer>
            </div>

            {/* Gr√°fico de barras horizontales - Unidades */}
            <div className="chart-card">
              <h3>Comparativa de Unidades</h3>
              <ResponsiveContainer width="100%" height={300}>
                <BarChart data={dataPorUnidad} layout="vertical">
                  <CartesianGrid strokeDasharray="3 3" />
                  <XAxis type="number" />
                  <YAxis dataKey="name" type="category" width={100} />
                  <Tooltip />
                  <Legend />
                  <Bar dataKey="cantidad" fill="#FFBB28" name="Indicadores" />
                </BarChart>
              </ResponsiveContainer>
            </div>
          </div>

          {/* Lista detallada */}
          <div className="detailed-list">
            <h3>üìã Listado Detallado por Meta</h3>
            {dataPorMeta.map((meta, idx) => (
              <div key={idx} className="meta-section">
                <h4>{meta.name} ({meta.cantidad} indicadores)</h4>
                <ul>
                  {indicadores
                    .filter(ind => `Meta ${ind.id_meta}` === meta.name)
                    .map(ind => (
                      <li key={ind.id_indicador}>
                        <strong>{ind.codigo_indicador}:</strong> {ind.nombre_indicador}
                        {ind.unidad_medida && ` (${ind.unidad_medida})`}
                      </li>
                    ))}
                </ul>
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
