import React, { useEffect, useState } from "react";
import { Bar } from "react-chartjs-2";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
} from "chart.js";
import "./App.css";

ChartJS.register(CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend);

function App() {
  const [indicadores, setIndicadores] = useState([]);
  const [form, setForm] = useState({
    codigo_indicador: "",
    nombre_indicador: "",
    unidad_medida: "",
    descripcion: "",
  });
  const [editId, setEditId] = useState(null);

  // üîπ Cargar datos al iniciar
  useEffect(() => {
    fetchIndicadores();
  }, []);

  const fetchIndicadores = async () => {
    try {
      const res = await fetch("http://localhost:5000/api/indicadores");
      const data = await res.json();
      setIndicadores(data);
    } catch (err) {
      console.error("Error al obtener indicadores:", err);
    }
  };

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (editId) {
        await fetch(`http://localhost:5000/api/indicadores/${editId}`, {
          method: "PUT",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify(form),
        });
      } else {
        await fetch("http://localhost:5000/api/indicadores", {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify(form),
        });
      }
      setForm({
        codigo_indicador: "",
        nombre_indicador: "",
        unidad_medida: "",
        descripcion: "",
      });
      setEditId(null);
      fetchIndicadores();
    } catch (err) {
      console.error("Error al guardar:", err);
    }
  };

  const handleEdit = (ind) => {
    setForm({
      codigo_indicador: ind.codigo_indicador,
      nombre_indicador: ind.nombre_indicador,
      unidad_medida: ind.unidad_medida,
      descripcion: ind.descripcion,
    });
    setEditId(ind.id_indicador);
  };

  const handleDelete = async (id) => {
    if (window.confirm("¬øSeguro que deseas eliminar este indicador?")) {
      await fetch(`http://localhost:5000/api/indicadores/${id}`, {
        method: "DELETE",
      });
      fetchIndicadores();
    }
  };

  // üîπ Datos para el gr√°fico
  const unidades = [...new Set(indicadores.map((i) => i.unidad_medida || "Sin unidad"))];
  const conteoPorUnidad = unidades.map(
    (u) => indicadores.filter((i) => i.unidad_medida === u).length
  );

  const data = {
    labels: unidades,
    datasets: [
      {
        label: "Cantidad de Indicadores",
        data: conteoPorUnidad,
        backgroundColor: "rgba(54, 162, 235, 0.6)",
        borderColor: "rgba(54, 162, 235, 1)",
        borderWidth: 1,
      },
    ],
  };

  const options = {
    responsive: true,
    plugins: {
      legend: { position: "top" },
      title: { display: true, text: "Indicadores por Unidad de Medida" },
    },
  };

  return (
    <div className="App">
      <h1>Indicadores de Salud</h1>

      {/* Formulario */}
      <form onSubmit={handleSubmit} className="formulario">
        <input
          type="text"
          name="codigo_indicador"
          placeholder="C√≥digo"
          value={form.codigo_indicador}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="nombre_indicador"
          placeholder="Nombre"
          value={form.nombre_indicador}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="unidad_medida"
          placeholder="Unidad"
          value={form.unidad_medida}
          onChange={handleChange}
        />
        <input
          type="text"
          name="descripcion"
          placeholder="Descripci√≥n"
          value={form.descripcion}
          onChange={handleChange}
        />

        <button type="submit">{editId ? "Actualizar" : "Agregar"}</button>
        {editId && (
          <button
            type="button"
            className="cancelar"
            onClick={() => {
              setForm({
                codigo_indicador: "",
                nombre_indicador: "",
                unidad_medida: "",
                descripcion: "",
              });
              setEditId(null);
            }}
          >
            Cancelar
          </button>
        )}
      </form>

      {/* Tabla */}
      <table>
        <thead>
          <tr>
            <th>C√≥digo</th>
            <th>Nombre</th>
            <th>Unidad</th>
            <th>Descripci√≥n</th>
            <th>Acciones</th>
          </tr>
        </thead>
        <tbody>
          {indicadores.map((ind) => (
            <tr key={ind.id_indicador}>
              <td>{ind.codigo_indicador}</td>
              <td>{ind.nombre_indicador}</td>
              <td>{ind.unidad_medida}</td>
              <td>{ind.descripcion}</td>
              <td>
                <button onClick={() => handleEdit(ind)}>‚úèÔ∏è</button>
                <button onClick={() => handleDelete(ind.id_indicador)}>üóëÔ∏è</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* üîπ Gr√°fico */}
      <div style={{ width: "600px", margin: "40px auto" }}>
        <Bar data={data} options={options} />
      </div>
    </div>
  );
}

export default App;
