import React, { useEffect, useState } from "react";
import "./App.css";

function App() {
  const [indicadores, setIndicadores] = useState([]);
  const [form, setForm] = useState({
    codigo_indicador: "",
    nombre_indicador: "",
    unidad_medida: "",
    descripcion: "",
  });
  const [editId, setEditId] = useState(null);

  //  Cargar los datos al iniciar
  useEffect(() => {
    fetchIndicadores();
  }, []);

  const fetchIndicadores = async () => {
    try {
      const res = await fetch("http://localhost:5000/api/indicadores");
      const data = await res.json();
      setIndicadores(data);
    } catch (err) {
      console.error("Error al obtener indicadores:", err);
    }
  };

  //  Manejar cambios en el formulario
  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  //  Crear o actualizar
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (editId) {
        // actualizar
        await fetch(`http://localhost:5000/api/indicadores/${editId}`, {
          method: "PUT",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify(form),
        });
      } else {
        // crear
        await fetch("http://localhost:5000/api/indicadores", {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify(form),
        });
      }

      setForm({
        codigo_indicador: "",
        nombre_indicador: "",
        unidad_medida: "",
        descripcion: "",
      });
      setEditId(null);
      fetchIndicadores();
    } catch (err) {
      console.error("Error al guardar:", err);
    }
  };

  //  Editar
  const handleEdit = (ind) => {
    setForm({
      codigo_indicador: ind.codigo_indicador,
      nombre_indicador: ind.nombre_indicador,
      unidad_medida: ind.unidad_medida,
      descripcion: ind.descripcion,
    });
    setEditId(ind.id_indicador);
  };

  //  Eliminar
  const handleDelete = async (id) => {
    if (window.confirm("驴Seguro que deseas eliminar este indicador?")) {
      await fetch(`http://localhost:5000/api/indicadores/${id}`, {
        method: "DELETE",
      });
      fetchIndicadores();
    }
  };

  return (
    <div className="App">
      <h1>Indicadores de Salud</h1>

      {/* Formulario */}
      <form onSubmit={handleSubmit} className="formulario">
        <input
          type="text"
          name="codigo_indicador"
          placeholder="C贸digo"
          value={form.codigo_indicador}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="nombre_indicador"
          placeholder="Nombre"
          value={form.nombre_indicador}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="unidad_medida"
          placeholder="Unidad"
          value={form.unidad_medida}
          onChange={handleChange}
        />
        <input
          type="text"
          name="descripcion"
          placeholder="Descripci贸n"
          value={form.descripcion}
          onChange={handleChange}
        />

        <button type="submit">
          {editId ? "Actualizar" : "Agregar"}
        </button>
        {editId && (
          <button
            type="button"
            className="cancelar"
            onClick={() => {
              setForm({
                codigo_indicador: "",
                nombre_indicador: "",
                unidad_medida: "",
                descripcion: "",
              });
              setEditId(null);
            }}
          >
            Cancelar
          </button>
        )}
      </form>

      {/* Tabla */}
      <table>
        <thead>
          <tr>
            <th>C贸digo</th>
            <th>Nombre</th>
            <th>Unidad</th>
            <th>Descripci贸n</th>
            <th>Acciones</th>
          </tr>
        </thead>
        <tbody>
          {indicadores.map((ind) => (
            <tr key={ind.id_indicador}>
              <td>{ind.codigo_indicador}</td>
              <td>{ind.nombre_indicador}</td>
              <td>{ind.unidad_medida}</td>
              <td>{ind.descripcion}</td>
              <td>
                <button onClick={() => handleEdit(ind)}>锔</button>
                <button onClick={() => handleDelete(ind.id_indicador)}>
                  锔
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default App;
